function data = myload(file, verbose)if nargin==1    verbose = 1;end[pathstr,name,ext,versn] = fileparts(file);if verbosefprintf('loading %s ... ', file); % disp(['loading ' file ' ...']);endif ext == '.mat'   ftype = '-MAT';   data = load(file, ftype);else   ftype = '-ASCII';   data = load(file);endif ext == '.mat'   fields = fieldnames(data);   data = data.(fields{1});endif verbosedisp([sizestr(data) ' ' mat2str(size(data))]);end